import React, { useState } from "react";
import api from "./services/apiService";
import { setToken, getToken } from "./services/authService";
import { toast, ToastContainer } from "react-toastify";
import { useNavigate } from "react-router-dom"; // Importando useNavigate para redirecionamento
import "react-toastify/dist/ReactToastify.css";

const TokenFetcher = () => {
  const [apiKey, setApiKey] = useState("");
  const navigate = useNavigate(); // Usando o hook useNavigate

  const fetchToken = async () => {
    if (!apiKey) {
      toast.error("Por favor, insira a API Key");
      return;
    }

    try {
      const response = await api.get(`/Token?apiKey=${apiKey}`);
      const newToken = response.data;
      setToken(newToken);

      // Exibindo o toast de sucesso com a mensagem e o botão OK
      toast.success(
        <div>
          Token validado com sucesso
          <button
            onClick={() => navigate("/list-devices")} // Redirecionando ao clicar no botão OK
            className="ml-4 text-blue-500"
          >
            OK
          </button>
        </div>,
        {
          position: toast.POSITION.TOP_CENTER,
          autoClose: 5000, // A duração do toast
        }
      );
    } catch (error) {
      toast.error("Erro ao validar token");
      console.error("Erro:", error);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="p-6 w-96 bg-white shadow-lg rounded-lg">
        <h2 className="text-xl font-semibold mb-4">Validar API Key</h2>
        <input
          type="text"
          placeholder="Digite sua API Key"
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          className="mb-4 w-full p-2 border rounded"
        />
        <button onClick={fetchToken} className="w-full bg-blue-500 text-white p-2 rounded">
          Validar Token
        </button>
      </div>
      <ToastContainer />
    </div>
  );
};

export default TokenFetcher;
