import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ListRainfall = () => {
    const { serialNumber } = useParams();
    const [rainfallData, setRainfallData] = useState(null);
    const [error, setError] = useState(null);
    const token = localStorage.getItem("token");

    useEffect(() => {
      const fetchRainfallData = async () => {
        try {
          const response = await axios.get(
            `http://localhost:3001/get-by-serialNumber/${serialNumber}/rainfall`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );

          console.log("Dados de rainfall recebidos:", response.data);

          // Garante que response.data existe
          if (!response.data) {
            setError("Nenhum dado de rainfall encontrado para este dispositivo.");
            return;
          }

          // Garante que sensor ou sensorSoft existam e não estejam vazios
          const hasSensorData =
            (Array.isArray(response.data.sensor) && response.data.sensor.length > 0) ||
            (Array.isArray(response.data.sensorSoft) && response.data.sensorSoft.length > 0);

          if (!hasSensorData) {
            setError("Nenhum dado de rainfall encontrado para este dispositivo.");
            return;
          }

          setRainfallData(response.data);
        } catch (err) {
          console.error("Erro ao buscar dados de rainfall:", err);
          setError("Erro ao carregar dados.");
        }
      };

      fetchRainfallData();
    }, [serialNumber, token]);

    return (
      <div className="container">
        <h1>Dados de Rainfall do Sensor {serialNumber}</h1>

        {error ? (
          <p>{error}</p>
        ) : !rainfallData ? (
          <p>Carregando...</p>
        ) : (
          <div className="sensor-data">
            <h2>{rainfallData.deviceName}</h2>
            <p><strong>Status:</strong> {rainfallData.status}</p>
            <p><strong>Última atualização:</strong> {new Date(rainfallData.lastUpload).toLocaleString()}</p>
            <h3>Sensores Rainfall</h3>

            {/* Lista de sensores físicos */}
            {rainfallData.sensor && rainfallData.sensor.length > 0 && (
              <ul>
                {rainfallData.sensor.map((sensor) => (
                  <li key={sensor.sensorId}>
                    <strong>{sensor.sensorType}</strong> - {sensor.uom}
                  </li>
                ))}
              </ul>
            )}

            {/* Lista de sensores calculados */}
            {rainfallData.sensorSoft && rainfallData.sensorSoft.length > 0 && (
              <ul>
                {rainfallData.sensorSoft.map((sensor) => (
                  <li key={sensor.sensorId}>
                    <strong>{sensor.customName || sensor.sensorType}</strong> - {sensor.uom}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    );
  };

  export default ListRainfall;
