// src/pages/ListDevicesPage.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import { getToken } from "../services/authService"; // Importa a função para pegar o token

const ListDevicesPage = () => {
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDevices = async () => {
    const token = getToken(); // Pega o token do localStorage

    if (!token) {
      setError("Token não encontrado");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get("http://localhost:3001/list-all-devices", {
        headers: {
          Authorization: `Bearer ${token}`, // Passa o token no cabeçalho
        },
      });
      setDevices(response.data.data); // Armazena os dispositivos no estado
      setLoading(false);
    } catch (error) {
      setError("Erro ao carregar dispositivos");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDevices(); // Chama a função para buscar os dispositivos assim que a página carrega
  }, []);

  if (loading) {
    return <div>Carregando dispositivos...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Lista de Dispositivos</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Status</th>
            <th>Data de Instalação</th>
          </tr>
        </thead>
        <tbody>
          {devices.map((device) => (
            <tr key={device.deviceId}>
              <td>{device.deviceId}</td>
              <td>{device.deviceName}</td>
              <td>{device.status}</td>
              <td>{device.dateInstalled}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListDevicesPage;
