import React, { useEffect, useState } from "react";
import api from "../services/apiService";
import { toast, ToastContainer } from "react-toastify";

const ListDevicesPage = () => {
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDevices = async () => {
      try {
        setLoading(true);  // Começa o carregamento

        // Faz a requisição para o endpoint da API utilizando o token
        const response = await api.get("/list-all-devices");
        setDevices(response.data);  // Atualiza o estado com os dados recebidos

        toast.success("Dados carregados com sucesso!");  // Sucesso na requisição
      } catch (error) {
        setError("Erro ao carregar os dispositivos");
        toast.error("Erro ao carregar os dispositivos");  // Erro na requisição
        console.error("Erro:", error);
      } finally {
        setLoading(false);  // Finaliza o carregamento
      }
    };

    fetchDevices();  // Chama a função para buscar os dados
  }, []);

  if (loading) {
    return <div>Carregando...</div>;  // Exibe "Carregando..." enquanto os dados não são carregados
  }

  if (error) {
    return <div>{error}</div>;  // Exibe mensagem de erro caso algo tenha dado errado
  }

  return (
    <div className="p-6 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Lista de Dispositivos</h1>
      <pre className="bg-white p-4 rounded-lg shadow-lg">
        {JSON.stringify(devices, null, 2)}  {/* Exibe os dados no formato JSON */}
      </pre>
      <ToastContainer />
    </div>
  );
};

export default ListDevicesPage;
