import axios from "axios";
import { getToken } from "./authService";

// Função para criar uma instância do axios com a URL base configurável
const createApiInstance = (baseURL = "http://localhost:3001") => {
  const api = axios.create({
    baseURL: baseURL,  // URL dinâmica
  });

  // Intercepta todas as requisições e adiciona o token no cabeçalho
  api.interceptors.request.use(
    (config) => {
      const token = getToken();
      console.log("Token para requisição:", token); // Adicione este log

      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    },
    (error) => Promise.reject(error)
  );

  return api;
};

export default createApiInstance;
