import React, { useEffect, useState } from "react";
import axios from "axios";
import { getToken } from "../services/authService"; // Função para obter o token
import { ToastContainer, toast } from "react-toastify"; // Notificações
import "react-toastify/dist/ReactToastify.css"; // Estilos do Toast

const ListDevicesPage = () => {
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Função para buscar dispositivos
  const fetchDevices = async () => {
    const token = getToken(); // Recupera o token do localStorage

    if (!token) {
      toast.error("Token não encontrado. Faça login novamente.");
      setError("Token não encontrado.");
      setLoading(false);
      return;
    }

    console.log("Token recuperado:", token); // Debug do token

    try {
      const response = await axios.get("http://localhost:3001/list-all-devices", {
        headers: {
          Authorization: `Bearer ${token}`, // Passa o token no cabeçalho
        },
      });

      console.log("Resposta da API:", response.data); // Debug da resposta

      if (!response.data || !response.data.body || !response.data.body.data) {
        throw new Error("Dados inválidos recebidos da API");
      }

      setDevices(Object.values(response.data.body.data)); // Converte os dados para array
      setLoading(false);
    } catch (error) {
      console.error("Erro na requisição:", error);
      setError("Erro ao carregar dispositivos.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDevices(); // Busca dispositivos ao carregar a página
  }, []);

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="p-6 w-4/5 bg-white shadow-lg rounded-lg">
        <h1 className="text-2xl font-semibold mb-4">Lista de Dispositivos</h1>

        {loading ? (
          <p>Carregando dispositivos...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : devices.length > 0 ? (
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                <th className="border p-2">ID</th>
                <th className="border p-2">Nome</th>
                <th className="border p-2">Status</th>
                <th className="border p-2">Data de Instalação</th>
              </tr>
            </thead>
            <tbody>
              {devices.map((device) => (
                <tr key={device.deviceId} className="text-center">
                  <td className="border p-2">{device.deviceId}</td>
                  <td className="border p-2">{device.deviceName}</td>
                  <td className="border p-2">{device.status}</td>
                  <td className="border p-2">{device.dateInstalled}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-500">Nenhum dispositivo encontrado.</p>
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default ListDevicesPage;
