import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { FaCheckCircle, FaTimesCircle } from "react-icons/fa";
import "./a.css";

const ListDevicesPage = () => {
  const [token] = useState(localStorage.getItem("token") || "");
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchDevices = async () => {
    try {
      const response = await axios.get("http://localhost:3001/list-all-devices", {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("Resposta completa da API:", response.data);

      if (!response.data || !response.data.body || !response.data.body.data) {
        console.error("Dados inválidos recebidos:", response.data);
        setError("Dados inválidos recebidos da API");
        setLoading(false);
        return;
      }

      const devicesList = Object.values(response.data.body.data);
      setDevices(devicesList);
      setLoading(false);
    } catch (error) {
      setError("Erro ao carregar dispositivos");
      setLoading(false);
      console.error("Erro na requisição:", error);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, []);


  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Lista de Dispositivos</h1>

      {loading ? (
        <div className="text-lg text-gray-600">Carregando dispositivos...</div>
      ) : error ? (
        <div className="text-red-500">{error}</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 w-full max-w-7xl">
          {devices.length > 0 ? (
            devices.map((device) => (
              <div
                key={device.deviceId}
                className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all transform hover:scale-105"
              >
                <div className="flex items-center justify-between">
                  <h2 className="text-lg font-semibold text-gray-700">{device.deviceName}</h2>
                  {device.status === "Ativo" ? (
                    <FaCheckCircle className="text-green-500 text-2xl" />
                  ) : (
                    <FaTimesCircle className="text-red-500 text-2xl" />
                  )}
                </div>
                <p className="text-gray-600 mt-2"><strong>Status:</strong> {device.status}</p>
                <p className="text-gray-600"><strong>Última atualização:</strong> {new Date(device.lastUpload).toLocaleString()}</p>
              </div>
            ))
          ) : (
            <div className="col-span-full text-center text-gray-500">Nenhum dispositivo encontrado</div>
          )}
        </div>
      )}
    </div>
  );
};

export default ListDevicesPage;
