import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const ListRainfall = () => {
    const { serialNumber } = useParams(); // Pega o serialNumber da URL
    const [rainfallData, setRainfallData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [token] = useState(localStorage.getItem("token") || "");

    useEffect(() => {
      const fetchRainfallData = async () => {
        try {
          const response = await axios.get(
            `http://localhost:3001/get-by-serialNumber/${serialNumber}/rainfall`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );

          console.log("Dados de rainfall:", response.data);

          if (response.data && response.data.body) {
            setRainfallData(response.data.body);
          } else {
            setError("Nenhum dado de rainfall encontrado.");
          }

          setLoading(false);
        } catch (error) {
          setError("Erro ao carregar dados de rainfall.");
          setLoading(false);
          console.error("Erro na requisição:", error);
        }
      };

      fetchRainfallData();
    }, [serialNumber, token]); // Recarrega a requisição sempre que o serialNumber mudar

    return (
        <div className="container">
          <h1>Dados de Rainfall do Sensor {serialNumber}</h1>

          {error ? (
            <p>{error}</p>
          ) : !rainfallData ? (
            <p>Carregando...</p>
          ) : (
            <div className="sensor-data">
              <h2>{rainfallData.deviceName}</h2>
              <p><strong>Status:</strong> {rainfallData.status}</p>
              <p><strong>Última atualização:</strong> {new Date(rainfallData.lastUpload).toLocaleString()}</p>
              <h3>Sensores Rainfall</h3>

              {rainfallData.sensor.length > 0 && (
                <ul>
                  {rainfallData.sensor.map((sensor) => (
                    <li key={sensor.sensorId}>
                      <strong>{sensor.sensorType}</strong> - {sensor.uom}
                    </li>
                  ))}
                </ul>
              )}

              {rainfallData.sensorSoft.length > 0 && (
                <ul>
                  {rainfallData.sensorSoft.map((sensor) => (
                    <li key={sensor.sensorId}>
                      <strong>{sensor.customName || sensor.sensorType}</strong> - {sensor.uom}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </div>
      );
    };

    export default ListRainfall;
