import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "../css/SoftSensorPageAtual.css";

const formatDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
};

const SoftSensorPageAtual = () => {
    const { sensorId } = useParams();
    const [sensorData, setSensorData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [offset, setOffset] = useState(0);
    const [hasMoreData, setHasMoreData] = useState(true);
    const isFetching = useRef(false);

    useEffect(() => {
        const fetchSensorInfo = async () => {
            if (isFetching.current || !hasMoreData) return;
            isFetching.current = true;

            try {
                const token = localStorage.getItem("token");
                const endDate = new Date();
                const startDate = new Date(endDate);
                startDate.setDate(endDate.getDate() - 1);

                const formattedStartDate = formatDate(startDate);
                const formattedEndDate = formatDate(endDate);

                const response = await axios.get(
                    `http://localhost:3001/get-info-sensor?startDate=${formattedStartDate}&endDate=${formattedEndDate}&offset=${offset}&softSensorId=${sensorId}`,
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );

                console.log("Dados do sensor recebidos:", response.data);
                const newData = response.data?.data || [];

                if (newData.length > 0) {
                    setSensorData((prevData) => [...prevData, ...newData]);
                    setOffset((prevOffset) => prevOffset + 1);
                } else {
                    setHasMoreData(false);
                }
            } catch (err) {
                console.error("Erro na requisição:", err);
                setError("Erro ao carregar dados.");
            } finally {
                setLoading(false);
                isFetching.current = false;
            }
        };

        fetchSensorInfo();
    }, [sensorId, offset]);

    const handleLoadMore = () => {
        if (hasMoreData) {
            setOffset((prevOffset) => prevOffset + 1);
        }
    };

    return (
        <div className="container">
            <h1>Detalhes do Sensor {sensorId}</h1>
            {loading && <p>Carregando...</p>}
            {error && <p>{error}</p>}

            <table border="1">
                <thead>
                    <tr>
                        <th>Data da Leitura</th>
                        <th>Valor do Sensor</th>
                    </tr>
                </thead>
                <tbody>
                    {sensorData.length > 0 ? (
                        sensorData.map((item, index) => (
                            <tr key={index}>
                                <td>{new Date(item.readingDate).toLocaleString()}</td>
                                <td>{item.sensorValue.toFixed(2)}</td>
                            </tr>
                        ))
                    ) : (
                        !loading && <tr><td colSpan="2">Nenhum dado disponível</td></tr>
                    )}
                </tbody>
            </table>

            {hasMoreData && (
                <button className="btn-load-more" onClick={handleLoadMore}>
                    Carregar Mais
                </button>
            )}
        </div>
    );
};

export default SoftSensorPageAtual;
